import React, { useEffect, useRef, useState } from "react";
import { db } from "./firebase";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp } from "firebase/firestore";

// Deterministic pseudo-random for consistent placement
function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

function getSeed(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash);
  }
  return hash;
}

// Collision detection for rectangles
function isOverlapping(a, b) {
  return !(
    a.left + a.width < b.left ||
    a.left > b.left + b.width ||
    a.top + a.height < b.top ||
    a.top > b.top + b.height
  );
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [viewport, setViewport] = useState({ width: window.innerWidth, height: window.innerHeight });
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const q = query(collection(db, "messages"), orderBy("timestamp", "asc"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const msgs = [];
      querySnapshot.forEach((doc) => {
        msgs.push({ id: doc.id, ...doc.data() });
      });
      setMessages(msgs);
    });
    return unsubscribe;
  }, []);

  // Update viewport size on resize
  useEffect(() => {
    const handleResize = () => {
      setViewport({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const minFontSize = 1.1;
  const maxFontSize = 2.3;
  const maxWidth = Math.min(0.7 * viewport.width, 400); // Responsive max width
  const lineHeight = 1.2;
  const maxTries = 30;

  // Estimate text height based on font size and length
  function estimateTextHeight(text, fontSize) {
    // Estimate number of lines
    const charsPerLine = Math.floor(maxWidth / (fontSize * 16));
    const lines = Math.ceil(text.length / charsPerLine);
    return lines * fontSize * 16 * lineHeight;
  }

  // Place messages without overlap, always fully visible
  function getScatteredMessages(messages) {
    const placed = [];
    messages.forEach((msg, idx) => {
      const seed = getSeed(msg.id || "" + idx);
      const fontSize = minFontSize + seededRandom(seed + 2) * (maxFontSize - minFontSize);
      const width = maxWidth;
      const height = estimateTextHeight(msg.text, fontSize);
      let found = false;
      let left, top;
      for (let attempt = 0; attempt < maxTries; attempt++) {
        const randX = seededRandom(seed + attempt) * 0.8 + 0.1; // 0.1 to 0.9
        const randY = seededRandom(seed + 100 + attempt) * 0.8 + 0.1;
        // Clamp so message is always fully visible
        left = randX * (viewport.width - width);
        top = randY * (viewport.height - height - 70); // 70px for input at bottom
        left = Math.max(0, Math.min(left, viewport.width - width));
        top = Math.max(0, Math.min(top, viewport.height - height - 70));
        const rect = { left, top, width, height };
        if (!placed.some((p) => isOverlapping(rect, p))) {
          found = true;
          placed.push({ ...rect, fontSize, text: msg.text, id: msg.id });
          break;
        }
      }
      if (!found) {
        // Place anyway (may overlap)
        placed.push({ left, top, width, height, fontSize, text: msg.text, id: msg.id });
      }
    });
    return placed;
  }

  const scatteredMessages = getScatteredMessages(messages);

  const sendMessage = async (e) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;
    await addDoc(collection(db, "messages"), {
      text: trimmed,
      timestamp: serverTimestamp(),
    });
    setInput("");
  };

  return (
    <div className="flex flex-col h-screen bg-black text-white">
      <div
        className="flex-1 relative overflow-auto"
        style={{
          width: "100%",
          height: "100%",
          minHeight: 0,
          background: "black",
        }}
      >
        <div
          style={{
            position: "relative",
            width: viewport.width,
            height: viewport.height,
            margin: "0 auto",
          }}
        >
          {scatteredMessages.map((msg) => (
            <div
              key={msg.id}
              style={{
                position: "absolute",
                left: msg.left,
                top: msg.top,
                fontSize: `${msg.fontSize}em`,
                color: "white",
                maxWidth: maxWidth,
                wordBreak: "break-word",
                pointerEvents: "auto",
                userSelect: "text",
                fontWeight: 400,
                opacity: 0.95,
                lineHeight,
              }}
            >
              {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>
      <form
        onSubmit={sendMessage}
        className="w-full flex items-center px-4 py-3 border-t border-gray-800 bg-black"
        autoComplete="off"
        style={{ position: "sticky", bottom: 0, zIndex: 10 }}
      >
        <input
          className="flex-1 bg-transparent border border-gray-700 rounded px-4 py-2 text-white focus:outline-none focus:border-white placeholder-gray-500"
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Send a message into the void..."
          autoFocus
        />
        <button
          type="submit"
          className="ml-3 px-4 py-2 bg-white text-black rounded hover:bg-gray-200 transition"
        >
          Send
        </button>
      </form>
    </div>
  );
}
