import React, { useState, useEffect, useRef, useMemo } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    // inputRef.current?.focus(); // We'll move focus logic to useEffect
  };

  // Refocus input when message is cleared
  useEffect(() => {
    if (message === '' && inputRef.current) {
      inputRef.current.focus();
    }
  }, [message]);

  return (
    <div
      className="fixed left-1/2 bottom-8 transform -translate-x-1/2 flex flex-col items-center"
      style={{ width: '25vw', minWidth: 220, maxWidth: 400, zIndex: 10 }}
    >
      <div className="w-full bg-black rounded-lg border border-white flex items-center px-2 py-2">
        <input
          ref={inputRef}
          className="flex-1 bg-black text-white border-none outline-none text-base placeholder-gray-500 px-2 py-2"
          type="text"
          maxLength={500}
          placeholder="Speak into The Void..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          aria-label="Type your message to send to The Void"
          onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
          disabled={loading}
          autoFocus
        />
        <button
          className="ml-2 text-white text-base font-bold px-3 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
          onClick={handleSend}
          disabled={loading || !message.trim() || message.length > 500}
        >
          Send
        </button>
      </div>
    </div>
  );
}

// Deterministic pseudo-random for consistent placement
function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

// Normal-like distribution centered at 0
function normalRandom(seed) {
  // Box-Muller transform with two seeds
  const u1 = seededRandom(seed);
  const u2 = seededRandom(seed + 1000);
  return Math.sqrt(-2.0 * Math.log(u1 + 0.0001)) * Math.cos(2.0 * Math.PI * u2) / 2; // ~-1 to 1, mostly near 0
}

// Fisher-Yates shuffle with seed
function seededShuffle(array, seed) {
  const arr = array.slice();
  let m = arr.length, t, i;
  let s = seed;
  while (m) {
    s = (s * 9301 + 49297) % 233280;
    i = Math.floor((s / 233280) * m--);
    t = arr[m];
    arr[m] = arr[i];
    arr[i] = t;
  }
  return arr;
}

// Animation frame hook
function useAnimationFrame() {
  const [time, setTime] = useState(0);
  useEffect(() => {
    let frame;
    let start = performance.now();
    const animate = () => {
      setTime(performance.now() - start);
      frame = requestAnimationFrame(animate);
    };
    frame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frame);
  }, []);
  return time;
}

function MessageList({ messages }) {
  const containerRef = useRef(null);
  const [userScroll, setUserScroll] = useState(0); // user's scroll offset
  const [viewport, setViewport] = useState({ width: window.innerWidth, height: window.innerHeight });
  const time = useAnimationFrame();

  // Assign random (x, y, z) and animation params to each message, memoized by message id
  const floatingMessages = useMemo(() => {
    return messages.map((msg, idx) => {
      const seed = msg.id.split('').reduce((acc, c) => acc + c.charCodeAt(0), 0) + idx;
      // Cluster X/Y toward center using normal distribution
      const randX = normalRandom(seed) * 0.5; // -0.5 to 0.5, mostly near 0
      const randY = normalRandom(seed + 1) * 0.5;
      // Place the first 10 messages right in front of the camera
      let randZ;
      if (idx < 10) {
        randZ = 0.2 + idx * 0.1; // 0.2, 0.3, ..., 1.1
      } else {
        randZ = 1.2 + ((idx - 10) / Math.max(1, messages.length - 11)) * 1.0; // 1.2 to 2.2
      }
      // Animation: random amplitude, frequency, phase
      const ampX = 0.05 + seededRandom(seed + 3) * 0.08; // 0.05-0.13
      const ampY = 0.05 + seededRandom(seed + 4) * 0.08;
      const freqX = 0.0002 + seededRandom(seed + 5) * 0.0003; // 0.0002-0.0005
      const freqY = 0.0002 + seededRandom(seed + 6) * 0.0003;
      const phaseX = seededRandom(seed + 7) * Math.PI * 2;
      const phaseY = seededRandom(seed + 8) * Math.PI * 2;
      return {
        ...msg,
        randX,
        randY,
        randZ,
        ampX,
        ampY,
        freqX,
        freqY,
        phaseX,
        phaseY,
      };
    });
  }, [messages]);

  // The total "depth" of the void
  const depth = Math.max(floatingMessages.length * 0.25 + 1, 2);
  // The total height of the scrollable area
  const totalHeight = depth * 1000 + viewport.height;

  // Set initial scroll to the start of the void
  useEffect(() => {
    const initialScroll = 0;
    if (containerRef.current) {
      containerRef.current.scrollTop = initialScroll;
      setUserScroll(initialScroll);
    }
  }, [depth]);

  // Update viewport size on resize
  useEffect(() => {
    const handleResize = () => {
      setViewport({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // User scroll handler (speed control)
  const handleScroll = (e) => {
    setUserScroll(e.target.scrollTop);
  };

  // Show up to 10 visible messages at a time, sorted by z, with overlap prevention
  const getVisibleMessages = () => {
    if (!messages || messages.length === 0) return [];
    const visible = [];
    const placed = [];
    const scrollZ = userScroll / 1000;
    // Sort by z (nearest first)
    const sorted = floatingMessages
      .map(msg => ({
        ...msg,
        z: msg.randZ * depth - scrollZ
      }))
      .filter(msg => msg.z > 0.01 && msg.z < 3.5) // expanded range
      .sort((a, b) => a.z - b.z);
    for (const msg of sorted) {
      const perspective = 900;
      const animX = msg.ampX * Math.sin(time * msg.freqX + msg.phaseX);
      const animY = msg.ampY * Math.cos(time * msg.freqY + msg.phaseY);
      const scale = Math.min(8, Math.max(0.2, perspective / (msg.z * 1000)));
      const centerX = viewport.width / 2;
      const centerY = viewport.height / 2;
      const x = centerX + (msg.randX + animX) * centerX * 0.8 * scale;
      const y = centerY + (msg.randY + animY) * centerY * 0.8 * scale + msg.z * 400;
      // Looser overlap
      let tooClose = false;
      for (const p of placed) {
        const dx = x - p.x;
        const dy = y - p.y;
        const minDist = 30 * (scale + p.scale) / 2;
        if (dx * dx + dy * dy < minDist * minDist) {
          tooClose = true;
          break;
        }
      }
      if (tooClose) continue;
      placed.push({ x, y, scale });
      let opacity = Math.max(0.1, Math.min(1, 1.2 - msg.z));
      let blur = 0;
      if (msg.z < 0.12) {
        opacity = Math.max(0, (msg.z - 0.01) / 0.11);
        blur = 16 * (1 - opacity) + 2;
      } else {
        blur = Math.max(0, (msg.z - 0.2) * 6);
      }
      visible.push({
        id: msg.id,
        text: msg.text,
        style: {
          position: 'absolute',
          left: x,
          top: y + scrollZ * 1000,
          transform: `translate(-50%, -50%) scale(${scale})`,
          opacity,
          color: 'white',
          fontWeight: 300,
          fontSize: '2rem',
          pointerEvents: 'none',
          whiteSpace: 'pre-line',
          textAlign: 'center',
          filter: `blur(${blur}px)`
        }
      });
    }
    return visible;
  };

  return (
    <div
      ref={containerRef}
      className="fixed inset-0 overflow-y-auto"
      style={{ height: '100vh', width: '100vw', background: 'radial-gradient(circle, #000000, #1a1a1a)' }}
      onScroll={handleScroll}
    >
      <div className="container mx-auto p-4">
        <h1 className="text-4xl font-bold text-white text-center mb-4">The Void</h1>
        <div className="grid gap-4">
          {getVisibleMessages().map(msg => (
            <div key={msg.id} style={msg.style}>
              {msg.text}
            </div>
          ))}
        </div>
      </div>
      <MessageInput onSend={onSend} loading={loading} />
    </div>
  );
}

export default MessageList;
