import React, { useState, useEffect, useRef, useMemo } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    // inputRef.current?.focus(); // We'll move focus logic to useEffect
  };

  // Refocus input when message is cleared
  useEffect(() => {
    if (message === '' && inputRef.current) {
      inputRef.current.focus();
    }
  }, [message]);

  return (
    <div
      className="fixed left-1/2 bottom-8 transform -translate-x-1/2 flex flex-col items-center"
      style={{ width: '25vw', minWidth: 220, maxWidth: 400, zIndex: 10 }}
    >
      <div className="w-full bg-black rounded-lg border border-white flex items-center px-2 py-2">
        <input
          ref={inputRef}
          className="flex-1 bg-black text-white border-none outline-none text-base placeholder-gray-500 px-2 py-2"
          type="text"
          maxLength={500}
          placeholder="Speak into The Void..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          aria-label="Type your message to send to The Void"
          onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
          disabled={loading}
          autoFocus
        />
        <button
          className="ml-2 text-white text-base font-bold px-3 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
          onClick={handleSend}
          disabled={loading || !message.trim() || message.length > 500}
        >
          Send
        </button>
      </div>
    </div>
  );
}

// Deterministic pseudo-random for consistent placement
function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

// Normal-like distribution centered at 0
function normalRandom(seed) {
  // Box-Muller transform with two seeds
  const u1 = seededRandom(seed);
  const u2 = seededRandom(seed + 1000);
  return Math.sqrt(-2.0 * Math.log(u1 + 0.0001)) * Math.cos(2.0 * Math.PI * u2) / 2; // ~-1 to 1, mostly near 0
}

// Fisher-Yates shuffle with seed
function seededShuffle(array, seed) {
  const arr = array.slice();
  let m = arr.length, t, i;
  let s = seed;
  while (m) {
    s = (s * 9301 + 49297) % 233280;
    i = Math.floor((s / 233280) * m--);
    t = arr[m];
    arr[m] = arr[i];
    arr[i] = t;
  }
  return arr;
}

// Animation frame hook
function useAnimationFrame() {
  const [time, setTime] = useState(0);
  useEffect(() => {
    let frame;
    let start = performance.now();
    const animate = () => {
      setTime(performance.now() - start);
      frame = requestAnimationFrame(animate);
    };
    frame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frame);
  }, []);
  return time;
}

function MessageList({ messages }) {
  const containerRef = useRef(null);
  const [userScroll, setUserScroll] = useState(0); // user's scroll offset
  const [viewport, setViewport] = useState({ width: window.innerWidth, height: window.innerHeight });
  const time = useAnimationFrame();

  // The total "depth" of a segment
  const segmentDepth = Math.max(messages.length * 0.25 + 1, 2);
  // The total height of the scrollable area
  const totalHeight = segmentDepth * 1000 * 100 + viewport.height; // 100 segments for practical infinite scroll

  // Set initial scroll to center of the void
  useEffect(() => {
    const initialScroll = (segmentDepth * 1000 * 50); // middle segment
    if (containerRef.current) {
      containerRef.current.scrollTop = initialScroll;
      setUserScroll(initialScroll);
    }
  }, [segmentDepth]);

  // Update viewport size on resize
  useEffect(() => {
    const handleResize = () => {
      setViewport({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // User scroll handler (speed control)
  const handleScroll = (e) => {
    setUserScroll(e.target.scrollTop);
  };

  // Show 10 slots at a time, each with a random message from a shuffled segment
  const getVisibleMessages = () => {
    if (!messages || messages.length === 0) return [];
    const visible = [];
    const placed = [];
    const scrollZ = userScroll / 1000;
    const minZ = scrollZ + 0.01;
    const maxZ = scrollZ + 2.5;
    for (let slot = 0; slot < 50; slot++) {
      const z = minZ + (maxZ - minZ) * (slot / 50);
      const segmentIdx = Math.floor(z / segmentDepth);
      const shuffled = seededShuffle(messages, segmentIdx + 12345);
      const msgIdx = slot % shuffled.length;
      const msg = shuffled[msgIdx];
      if (!msg) continue;
      const seed = msg.id.split('').reduce((acc, c) => acc + c.charCodeAt(0), 0) + segmentIdx;
      const randX = normalRandom(seed) * 0.5;
      const randY = normalRandom(seed + 1) * 0.5;
      const ampX = 0.05 + seededRandom(seed + 3) * 0.08;
      const ampY = 0.05 + seededRandom(seed + 4) * 0.08;
      const freqX = 0.0002 + seededRandom(seed + 5) * 0.0003;
      const freqY = 0.0002 + seededRandom(seed + 6) * 0.0003;
      const phaseX = seededRandom(seed + 7) * Math.PI * 2;
      const phaseY = seededRandom(seed + 8) * Math.PI * 2;
      const animX = ampX * Math.sin(time * freqX + phaseX);
      const animY = ampY * Math.cos(time * freqY + phaseY);
      const perspective = 900;
      const scale = Math.min(8, Math.max(0.2, perspective / (z * 1000)));
      const centerX = viewport.width / 2;
      const centerY = viewport.height / 2;
      const x = centerX + (randX + animX) * centerX * 0.8 * scale;
      const y = centerY + (randY + animY) * centerY * 0.8 * scale + (z - scrollZ) * 400;
      // Overlap: reduce minDist
      let tooClose = false;
      for (const p of placed) {
        const dx = x - p.x;
        const dy = y - p.y;
        const minDist = 50 * (scale + p.scale) / 2;
        if (dx * dx + dy * dy < minDist * minDist) {
          tooClose = true;
          break;
        }
      }
      if (tooClose) continue;
      placed.push({ x, y, scale });
      let opacity = Math.max(0.15, Math.min(1, 1.2 - (z - scrollZ)));
      let blur = 0;
      if ((z - scrollZ) < 0.12) {
        opacity = Math.max(0, ((z - scrollZ) - 0.01) / 0.11);
        blur = 16 * (1 - opacity) + 2;
      } else {
        blur = Math.max(0, ((z - scrollZ) - 0.2) * 6);
      }
      visible.push({
        id: msg.id + '-' + segmentIdx + '-' + slot,
        text: msg.text,
        style: {
          position: 'absolute',
          left: x,
          top: y + scrollZ * 1000,
          transform: `translate(-50%, -50%) scale(${scale})`,
          opacity,
          color: 'white',
          fontWeight: 300,
          fontSize: '2rem',
          pointerEvents: 'none',
          whiteSpace: 'pre-line',
          textAlign: 'center',
          filter: `blur(${blur}px)`
        }
      });
    }
    // Fallback: if nothing is visible, show the first message at the center
    if (visible.length === 0 && messages.length > 0) {
      visible.push({
        id: messages[0].id + '-fallback',
        text: messages[0].text,
        style: {
          position: 'absolute',
          left: viewport.width / 2,
          top: viewport.height / 2,
          transform: 'translate(-50%, -50%) scale(1.2)',
          opacity: 0.7,
          color: 'white',
          fontWeight: 300,
          fontSize: '2rem',
          pointerEvents: 'none',
          whiteSpace: 'pre-line',
          textAlign: 'center',
          filter: 'blur(0px)'
        }
      });
    }
    return visible;
  };

  return (
    <div
      ref={containerRef}
      className="relative w-full h-full overflow-y-scroll bg-black"
      style={{ height: '100vh', paddingBottom: 100 }}
      tabIndex={0}
      onScroll={handleScroll}
    >
      <div style={{ position: 'relative', height: totalHeight }}>
        {floatingMessages.length === 0 && (
          <div style={{ position: 'absolute', top: '40%', left: '50%', transform: 'translate(-50%, 0)', color: '#fff', opacity: 0.3, fontSize: '2rem', textAlign: 'center' }}>
            The Void is silent... for now.
          </div>
        )}
        {getVisibleMessages().map((msg) => (
          <div key={msg.id} style={msg.style}>
            {msg.text}
          </div>
        ))}
      </div>
    </div>
  );
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('timestamp', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messageData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(messageData);
    }, (error) => {
      console.error('Error fetching messages:', error);
    });
    return () => unsubscribe();
  }, []);

  const handleSend = async (text) => {
    setLoading(true);
    try {
      await import('firebase/firestore').then(({ collection, addDoc, serverTimestamp }) =>
        addDoc(collection(db, 'messages'), {
          text,
          timestamp: serverTimestamp(),
        })
      );
    } catch (e) {
      alert('Failed to send message. Please try again.');
      console.error('Error adding message:', e);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-black w-full h-screen min-h-screen overflow-hidden">
      <MessageList messages={messages} />
      <MessageInput onSend={handleSend} loading={loading} />
    </div>
  );
}
