import React, { useEffect, useRef, useState } from "react";
import { db } from "./firebase";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp } from "firebase/firestore";

function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

function getSeed(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash);
  }
  return hash;
}

function randomBetween(a, b) {
  return a + Math.random() * (b - a);
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [viewport, setViewport] = useState({ width: window.innerWidth, height: window.innerHeight });
  const [floating, setFloating] = useState([]); // Active floating messages
  const [scrollSpeed, setScrollSpeed] = useState(0); // User scroll speed multiplier
  const animationRef = useRef();
  const nextIdRef = useRef(0);
  const prepopulatedRef = useRef(false);
  const prevWidthRef = useRef(window.innerWidth);
  const scrollContainerRef = useRef();
  const SCROLL_HEIGHT = 5000; // Virtual scroll area height

  // Firestore listener
  useEffect(() => {
    const q = query(collection(db, "messages"), orderBy("timestamp", "asc"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const msgs = [];
      querySnapshot.forEach((doc) => {
        msgs.push({ id: doc.id, ...doc.data() });
      });
      setMessages(msgs);
    });
    return unsubscribe;
  }, []);

  // Only update viewport, don't reset floating messages on resize
  useEffect(() => {
    const handleResize = () => {
      setViewport((prev) => {
        const newWidth = window.innerWidth;
        const oldWidth = prevWidthRef.current;
        setFloating((floaters) =>
          floaters.map((f) => {
            // Calculate ratio of previous X position
            const ratio = oldWidth > f.width ? f.x / (oldWidth - f.width) : 0;
            const newMsgWidth = Math.min(f.width, 0.6 * newWidth, 340);
            const newX = ratio * (newWidth - newMsgWidth);
            return {
              ...f,
              x: Math.max(0, Math.min(newX, newWidth - newMsgWidth)),
              width: newMsgWidth,
            };
          })
        );
        prevWidthRef.current = newWidth;
        return { width: newWidth, height: window.innerHeight };
      });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Scroll overlay logic
  useEffect(() => {
    const container = scrollContainerRef.current;
    if (!container) return;
    // Start in the middle for neutral speed
    container.scrollTop = SCROLL_HEIGHT / 2;
    function handleScroll() {
      // -1 (scroll to top) = max up speed, 0 = neutral, +1 (scroll to bottom) = max down speed
      const ratio = (container.scrollTop / (SCROLL_HEIGHT - viewport.height)) * 2 - 1;
      setScrollSpeed(ratio);
    }
    container.addEventListener("scroll", handleScroll);
    return () => container.removeEventListener("scroll", handleScroll);
  }, [viewport.height]);

  // Animation: floating messages rise up, speed affected by scroll
  useEffect(() => {
    if (!messages.length) return;
    let running = true;
    const minFontSize = 1.0;
    const maxFontSize = 2.0;
    const maxWidth = Math.min(0.6 * viewport.width, 340);
    const minSpeed = 40; // px/sec
    const maxSpeed = 90; // px/sec
    const spawnInterval = 700; // ms between spawns
    const maxFloating = Math.floor(viewport.height / 60) + 8;
    const baseMultiplier = 1.0;
    const scrollMultiplier = 2.5; // How much scroll affects speed

    function estimateTextHeight(text, fontSize) {
      const charsPerLine = Math.floor(maxWidth / (fontSize * 16));
      const lines = Math.ceil(text.length / charsPerLine);
      return lines * fontSize * 16 * 1.2;
    }

    function spawnMessage(yOverride) {
      if (!messages.length) return null;
      // Pick a random message
      const msgIdx = Math.floor(Math.random() * messages.length);
      const msg = messages[msgIdx];
      // Random X, font size, speed
      const fontSize = randomBetween(minFontSize, maxFontSize);
      const width = maxWidth;
      const height = estimateTextHeight(msg.text, fontSize);
      const x = randomBetween(0, viewport.width - width);
      const speed = randomBetween(minSpeed, maxSpeed); // px/sec
      const id = `floating-${nextIdRef.current++}`;
      return {
        id,
        text: msg.text,
        x,
        y: typeof yOverride === 'number' ? yOverride : viewport.height, // Start at bottom or custom
        fontSize,
        width,
        height,
        speed,
      };
    }

    let lastSpawn = Date.now();
    let floaters = [];
    let lastFrame = Date.now();

    // Pre-populate floaters only on first mount
    if (!prepopulatedRef.current && floating.length === 0) {
      const initialCount = Math.floor(viewport.height / 60) + 8;
      for (let i = 0; i < initialCount; i++) {
        // Random y between 0 (top) and viewport.height (bottom)
        const y = randomBetween(0, viewport.height);
        const msg = spawnMessage(y);
        if (msg) floaters.push(msg);
      }
      setFloating([...floaters]);
      prepopulatedRef.current = true;
    } else {
      floaters = [...floating];
    }

    function animate() {
      if (!running) return;
      const now = Date.now();
      const dt = (now - lastFrame) / 1000;
      lastFrame = now;
      // Calculate speed multiplier from scroll
      const speedMult = baseMultiplier + scrollMultiplier * scrollSpeed;
      // Move all floaters up (or down)
      floaters = floaters
        .map((f) => ({ ...f, y: f.y - f.speed * dt * speedMult }))
        .filter((f) => f.y + f.height > 0 && f.y < viewport.height * 1.2); // Remove if off top or far below
      // Spawn new if needed
      if (now - lastSpawn > spawnInterval && floaters.length < maxFloating) {
        const newMsg = spawnMessage();
        if (newMsg) {
          floaters.push(newMsg);
        }
        lastSpawn = now;
      }
      setFloating([...floaters]);
      animationRef.current = requestAnimationFrame(animate);
    }
    animationRef.current = requestAnimationFrame(animate);
    return () => {
      running = false;
      cancelAnimationFrame(animationRef.current);
    };
    // eslint-disable-next-line
  }, [messages, viewport.width, viewport.height, scrollSpeed]);

  const sendMessage = async (e) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;
    await addDoc(collection(db, "messages"), {
      text: trimmed,
      timestamp: serverTimestamp(),
    });
    setInput("");
  };

  return (
    <div className="flex flex-col h-screen bg-black text-white">
      {/* Scroll overlay for controlling animation speed */}
      <div
        ref={scrollContainerRef}
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          width: "100vw",
          height: "100vh",
          zIndex: 20,
          opacity: 0,
          pointerEvents: "auto",
          overflowY: "scroll",
        }}
        tabIndex={-1}
      >
        <div style={{ height: SCROLL_HEIGHT }} />
      </div>
      <div
        className="flex-1 relative overflow-hidden"
        style={{ width: "100%", height: "100%", minHeight: 0, background: "black" }}
      >
        {floating.map((msg) => (
          <div
            key={msg.id}
            style={{
              position: "absolute",
              left: msg.x,
              top: msg.y,
              fontSize: `${msg.fontSize}em`,
              color: "white",
              maxWidth: msg.width,
              wordBreak: "break-word",
              pointerEvents: "auto",
              userSelect: "text",
              fontWeight: 400,
              opacity: 0.95,
              lineHeight: 1.2,
              transition: "none",
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <form
        onSubmit={sendMessage}
        className="w-full flex items-center px-4 py-3 border-t border-gray-800 bg-black"
        autoComplete="off"
        style={{ position: "sticky", bottom: 0, zIndex: 10 }}
      >
        <input
          className="flex-1 bg-transparent border border-gray-700 rounded px-4 py-2 text-white focus:outline-none focus:border-white placeholder-gray-500"
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Send a message into the void..."
          autoFocus
        />
        <button
          type="submit"
          className="ml-3 px-4 py-2 bg-white text-black rounded hover:bg-gray-200 transition"
        >
          Send
        </button>
      </form>
    </div>
  );
}
