import React, { useState, useEffect, useRef } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    inputRef.current?.focus();
  };

  return (
    <div className="fixed bottom-0 left-0 w-full bg-black flex items-center px-4 py-3" style={{zIndex: 10}}>
      <input
        ref={inputRef}
        className="flex-1 bg-black text-white border-none outline-none text-lg placeholder-gray-500 px-2 py-3"
        type="text"
        maxLength={500}
        placeholder="Speak into The Void..."
        value={message}
        onChange={e => setMessage(e.target.value)}
        aria-label="Type your message to send to The Void"
        onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
        disabled={loading}
        autoFocus
      />
      <button
        className="ml-2 text-white text-lg font-bold px-4 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
        onClick={handleSend}
        disabled={loading || !message.trim() || message.length > 500}
      >
        Send
      </button>
    </div>
  );
}

function MessageList({ messages }) {
  const containerRef = useRef(null);

  // Calculate transform/opacity for each message based on its position in the scroll
  const [scroll, setScroll] = useState(0);
  const handleScroll = () => {
    setScroll(containerRef.current.scrollTop);
  };

  useEffect(() => {
    const ref = containerRef.current;
    if (ref) {
      ref.addEventListener('scroll', handleScroll);
      return () => ref.removeEventListener('scroll', handleScroll);
    }
  }, []);

  // Helper to get style for each message
  const getMessageStyle = (idx) => {
    // Messages further down the list are "closer" as you scroll
    const base = 80; // px between messages
    const y = idx * base - scroll;
    // The closer to the center (y ~ 0), the bigger and more opaque
    const center = 200; // px from center
    const scale = Math.max(0.7, 1.2 - Math.abs(y) / center);
    const opacity = Math.max(0.2, 1 - Math.abs(y) / (center * 1.2));
    return {
      transform: `translateY(${y}px) scale(${scale})`,
      opacity,
      transition: 'transform 0.2s, opacity 0.2s',
      position: 'absolute',
      left: '50%',
      width: '100%',
      maxWidth: '600px',
      transformOrigin: 'center',
      pointerEvents: 'none',
      textAlign: 'center',
      fontSize: '1.5rem',
      color: 'white',
      fontWeight: 300,
      filter: 'blur(' + (1 - opacity) * 2 + 'px)',
    };
  };

  return (
    <div
      ref={containerRef}
      className="relative w-full h-full overflow-y-scroll bg-black"
      style={{ height: '100vh', paddingBottom: 80 }}
      tabIndex={0}
    >
      <div style={{ position: 'relative', height: messages.length * 80 + 200 }}>
        {messages.length === 0 && (
          <div style={{ position: 'absolute', top: '40%', left: 0, width: '100%', textAlign: 'center', color: '#fff', opacity: 0.3, fontSize: '2rem' }}>
            The Void is silent... for now.
          </div>
        )}
        {messages.map((msg, idx) => (
          <div key={msg.id} style={getMessageStyle(idx)}>
            {msg.text}
          </div>
        ))}
      </div>
    </div>
  );
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('timestamp', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messageData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(messageData);
    }, (error) => {
      console.error('Error fetching messages:', error);
    });
    return () => unsubscribe();
  }, []);

  const handleSend = async (text) => {
    setLoading(true);
    try {
      await import('firebase/firestore').then(({ collection, addDoc, serverTimestamp }) =>
        addDoc(collection(db, 'messages'), {
          text,
          timestamp: serverTimestamp(),
        })
      );
    } catch (e) {
      alert('Failed to send message. Please try again.');
      console.error('Error adding message:', e);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-black w-full h-screen min-h-screen overflow-hidden">
      <MessageList messages={messages} />
      <MessageInput onSend={handleSend} loading={loading} />
    </div>
  );
}
