import React, { useState, useEffect, useRef } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    inputRef.current?.focus();
  };

  useEffect(() => {
    if (message === '' && inputRef.current) {
      inputRef.current.focus();
    }
  }, [message]);

  return (
    <div
      className="fixed left-1/2 bottom-8 transform -translate-x-1/2 flex flex-col items-center"
      style={{ width: '25vw', minWidth: 220, maxWidth: 400, zIndex: 10 }}
    >
      <div className="w-full bg-black rounded-lg border border-white flex items-center px-2 py-2">
        <input
          ref={inputRef}
          className="flex-1 bg-black text-white border-none outline-none text-base placeholder-gray-500 px-2 py-2"
          type="text"
          maxLength={500}
          placeholder="Speak into The Void..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          aria-label="Type your message to send to The Void"
          onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
          disabled={loading}
          autoFocus
        />
        <button
          className="ml-2 text-white text-base font-bold px-3 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
          onClick={handleSend}
          disabled={loading || !message.trim() || message.length > 500}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [scrollZ, setScrollZ] = useState(0);
  const containerRef = useRef(null);

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('timestamp', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messageData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(messageData);
    }, (error) => {
      console.error('Error fetching messages:', error);
    });
    return () => unsubscribe();
  }, []);

  // Evenly distribute messages along z-axis
  const depth = 2.0;
  const message3D = messages.map((msg, idx) => ({
    ...msg,
    z: 0.2 + (idx / Math.max(1, messages.length - 1)) * depth
  }));

  // Handle scroll to move forward/backward in z
  const handleScroll = (e) => {
    setScrollZ(e.target.scrollTop / 400); // tweak denominator for speed
  };

  // Set initial scroll to 0
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = 0;
      setScrollZ(0);
    }
  }, [messages.length]);

  // Perspective projection
  const perspective = 900;
  const centerX = window.innerWidth / 2;
  const centerY = window.innerHeight / 2;

  return (
    <div className="bg-black w-full h-screen min-h-screen overflow-hidden">
      <div
        ref={containerRef}
        className="relative w-full h-full overflow-y-scroll bg-black"
        style={{ height: '100vh', paddingBottom: 120 }}
        tabIndex={0}
        onScroll={handleScroll}
      >
        <div style={{ position: 'relative', height: '3000px' }}>
          {message3D.map((msg, idx) => {
            const z = msg.z - scrollZ;
            if (z < 0.01 || z > 2.5) return null; // only show messages in front of camera
            const scale = perspective / (perspective + z * 1000);
            const x = centerX;
            const y = centerY + z * 400;
            let opacity = Math.max(0.1, Math.min(1, 1.2 - z));
            let blur = 0;
            if (z < 0.12) {
              opacity = Math.max(0, (z - 0.01) / 0.11);
              blur = 16 * (1 - opacity) + 2;
            } else {
              blur = Math.max(0, (z - 0.2) * 6);
            }
            return (
              <div
                key={msg.id}
                style={{
                  position: 'absolute',
                  left: x,
                  top: y + scrollZ * 1000,
                  transform: `translate(-50%, -50%) scale(${scale})`,
                  opacity,
                  color: 'white',
                  fontWeight: 300,
                  fontSize: '2rem',
                  pointerEvents: 'none',
                  whiteSpace: 'pre-line',
                  textAlign: 'center',
                  filter: `blur(${blur}px)`,
                  width: '100vw',
                  maxWidth: 600,
                }}
              >
                {msg.text}
              </div>
            );
          })}
        </div>
      </div>
      <MessageInput onSend={async (text) => {
        setLoading(true);
        try {
          await import('firebase/firestore').then(({ collection, addDoc, serverTimestamp }) =>
            addDoc(collection(db, 'messages'), {
              text,
              timestamp: serverTimestamp(),
            })
          );
        } catch (e) {
          alert('Failed to send message. Please try again.');
          console.error('Error adding message:', e);
        } finally {
          setLoading(false);
        }
      }} loading={loading} />
    </div>
  );
}
