import React, { useState, useEffect, useRef, useMemo } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    inputRef.current?.focus();
  };

  return (
    <div className="fixed bottom-0 left-0 w-full bg-black flex items-center px-4 py-3" style={{zIndex: 10}}>
      <input
        ref={inputRef}
        className="flex-1 bg-black text-white border-none outline-none text-lg placeholder-gray-500 px-2 py-3"
        type="text"
        maxLength={500}
        placeholder="Speak into The Void..."
        value={message}
        onChange={e => setMessage(e.target.value)}
        aria-label="Type your message to send to The Void"
        onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
        disabled={loading}
        autoFocus
      />
      <button
        className="ml-2 text-white text-lg font-bold px-4 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
        onClick={handleSend}
        disabled={loading || !message.trim() || message.length > 500}
      >
        Send
      </button>
    </div>
  );
}

// Deterministic pseudo-random for consistent placement
function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

function MessageList({ messages }) {
  const containerRef = useRef(null);
  const [scrollZ, setScrollZ] = useState(0);
  const [viewport, setViewport] = useState({ width: window.innerWidth, height: window.innerHeight });

  // Assign random (x, y, z) to each message, memoized by message id
  const floatingMessages = useMemo(() => {
    return messages.map((msg, idx) => {
      // Use a seeded random based on message id for consistent placement
      const seed = msg.id.split('').reduce((acc, c) => acc + c.charCodeAt(0), 0) + idx;
      // X: -1 to 1 (left to right), Y: -1 to 1 (top to bottom), Z: 0 (close) to 1 (far)
      const randX = seededRandom(seed) * 2 - 1;
      const randY = seededRandom(seed + 1) * 2 - 1;
      const randZ = 0.2 + seededRandom(seed + 2) * 0.8; // 0.2 to 1.0
      return {
        ...msg,
        randX,
        randY,
        randZ,
      };
    });
  }, [messages]);

  // Update viewport size on resize
  useEffect(() => {
    const handleResize = () => {
      setViewport({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Scroll handler
  const handleScroll = (e) => {
    setScrollZ(e.target.scrollTop / 1000); // Each 1000px of scroll is 1.0 in z
  };

  // The total "depth" of the void
  const depth = Math.max(floatingMessages.length * 0.25 + 1, 2);
  // The total height of the scrollable area
  const totalHeight = depth * 1000 + viewport.height;

  // Perspective projection for each message with strong first-person effect
  const getMessageStyle = (msg) => {
    // Camera moves forward as you scroll (scrollZ)
    const z = msg.randZ * depth - scrollZ;
    if (z < 0.01) return { display: 'none' }; // Behind the camera
    const perspective = 900; // px
    // Dramatic scale: as z approaches 0, scale increases rapidly
    const scale = Math.min(8, Math.max(0.2, perspective / (z * 1000)));
    const centerX = viewport.width / 2;
    const centerY = viewport.height / 2;
    const x = centerX + msg.randX * centerX * 0.8 * scale;
    const y = centerY + msg.randY * centerY * 0.8 * scale + z * 400;
    // Opacity and blur based on depth
    let opacity = Math.max(0, Math.min(1, 1.2 - z));
    let blur = 0;
    // As z approaches 0.05, fade out and blur heavily
    if (z < 0.12) {
      opacity = Math.max(0, (z - 0.01) / 0.11); // fade out as it passes camera
      blur = 16 * (1 - opacity) + 2;
    } else {
      blur = Math.max(0, (z - 0.2) * 6);
    }
    return {
      position: 'absolute',
      left: x,
      top: y + scrollZ * 1000, // keep messages in the scrollable area
      transform: `translate(-50%, -50%) scale(${scale})`,
      opacity,
      color: 'white',
      fontWeight: 300,
      fontSize: '2rem',
      pointerEvents: 'none',
      whiteSpace: 'pre-line',
      textAlign: 'center',
      filter: `blur(${blur}px)`
    };
  };

  return (
    <div
      ref={containerRef}
      className="relative w-full h-full overflow-y-scroll bg-black"
      style={{ height: '100vh', paddingBottom: 100 }}
      tabIndex={0}
      onScroll={handleScroll}
    >
      <div style={{ position: 'relative', height: totalHeight }}>
        {floatingMessages.length === 0 && (
          <div style={{ position: 'absolute', top: '40%', left: '50%', transform: 'translate(-50%, 0)', color: '#fff', opacity: 0.3, fontSize: '2rem', textAlign: 'center' }}>
            The Void is silent... for now.
          </div>
        )}
        {floatingMessages.map((msg) => (
          <div key={msg.id} style={getMessageStyle(msg)}>
            {msg.text}
          </div>
        ))}
      </div>
    </div>
  );
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('timestamp', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messageData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(messageData);
    }, (error) => {
      console.error('Error fetching messages:', error);
    });
    return () => unsubscribe();
  }, []);

  const handleSend = async (text) => {
    setLoading(true);
    try {
      await import('firebase/firestore').then(({ collection, addDoc, serverTimestamp }) =>
        addDoc(collection(db, 'messages'), {
          text,
          timestamp: serverTimestamp(),
        })
      );
    } catch (e) {
      alert('Failed to send message. Please try again.');
      console.error('Error adding message:', e);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-black w-full h-screen min-h-screen overflow-hidden">
      <MessageList messages={messages} />
      <MessageInput onSend={handleSend} loading={loading} />
    </div>
  );
}
