import React, { useState, useEffect, useRef } from 'react';
import { collection, onSnapshot, orderBy, query } from 'firebase/firestore';
import { db } from './firebase';

function MessageInput({ onSend, loading }) {
  const [message, setMessage] = useState('');
  const inputRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || message.length > 500) return;
    await onSend(message.trim());
    setMessage('');
    inputRef.current?.focus();
  };

  useEffect(() => {
    if (message === '' && inputRef.current) {
      inputRef.current.focus();
    }
  }, [message]);

  return (
    <div
      className="fixed left-1/2 bottom-8 transform -translate-x-1/2 flex flex-col items-center"
      style={{ width: '25vw', minWidth: 220, maxWidth: 400, zIndex: 10 }}
    >
      <div className="w-full bg-black rounded-lg border border-white flex items-center px-2 py-2">
        <input
          ref={inputRef}
          className="flex-1 bg-black text-white border-none outline-none text-base placeholder-gray-500 px-2 py-2"
          type="text"
          maxLength={500}
          placeholder="Speak into The Void..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          aria-label="Type your message to send to The Void"
          onKeyDown={e => { if (e.key === 'Enter') handleSend(); }}
          disabled={loading}
          autoFocus
        />
        <button
          className="ml-2 text-white text-base font-bold px-3 py-2 bg-black border border-white rounded transition-opacity disabled:opacity-40"
          onClick={handleSend}
          disabled={loading || !message.trim() || message.length > 500}
        >
          Send
        </button>
      </div>
    </div>
  );
}

// Deterministic pseudo-random for consistent placement
function seededRandom(seed) {
  let x = Math.sin(seed) * 10000;
  return x - Math.floor(x);
}

export default function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userScroll, setUserScroll] = useState(0);
  const [scrollZ, setScrollZ] = useState(0);
  const containerRef = useRef(null);
  const animationRef = useRef();

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('timestamp', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messageData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(messageData);
    }, (error) => {
      console.error('Error fetching messages:', error);
    });
    return () => unsubscribe();
  }, []);

  // Evenly distribute messages along z-axis, add random X
  const depth = 20;
  const zStart = -10;
  const message3D = messages.map((msg, idx) => {
    const seed = msg.id.split('').reduce((acc, c) => acc + c.charCodeAt(0), 0) + idx;
    return {
      ...msg,
      z: zStart + (idx / Math.max(1, messages.length - 1)) * depth,
      randX: (seededRandom(seed) * 2 - 1) * 0.4 // -0.4 to 0.4 (as a fraction of width)
    };
  });

  // Animation: always move forward, scroll controls speed
  useEffect(() => {
    let last = performance.now();
    function animate(now) {
      const dt = now - last;
      last = now;
      // even slower base speed + scroll speed
      setScrollZ(z => z + dt * (0.0001 + userScroll / 200000));
      animationRef.current = requestAnimationFrame(animate);
    }
    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, [userScroll]);

  // Handle scroll to control speed
  const handleScroll = (e) => {
    setUserScroll(e.target.scrollTop);
  };

  // Set initial scroll to 0
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = 0;
      setUserScroll(0);
    }
  }, [messages.length]);

  // Perspective projection
  const perspective = 200; // much smaller for dramatic zoom
  const centerX = window.innerWidth / 2;
  const centerY = window.innerHeight / 2;
  const totalDepth = depth;

  return (
    <div className="bg-black w-full h-screen min-h-screen overflow-hidden" style={{ position: 'relative' }}>
      {/* 3D Messages Overlay */}
      <div style={{
        position: 'fixed',
        left: 0, top: 0, width: '100vw', height: '100vh',
        pointerEvents: 'none', zIndex: 1
      }}>
        {message3D.map((msg, idx) => {
          let z = ((msg.z - scrollZ) % totalDepth + totalDepth) % totalDepth;
          if (z < -100 || z > 5) return null;
          // Super zoom: scale blows up as z approaches -2.5
          const zForScale = Math.max(z, -2.4); // avoid division by zero
          const scale = 10 / (zForScale + 2.5);
          const x = centerX + msg.randX * centerX * scale;
          const y = centerY;
          let opacity = Math.max(0.1, Math.min(1, 1.2 - z));
          let blur = 0;
          if (z < 0.12) {
            opacity = Math.max(0, (z - 0.01) / 0.11);
            blur = 16 * (1 - opacity) + 2;
          } else {
            blur = Math.max(0, (z - 0.2) * 6);
          }
          return (
            <div
              key={msg.id}
              style={{
                position: 'absolute',
                left: x,
                top: y,
                transform: `translate(-50%, -50%) scale(${scale})`,
                opacity,
                color: 'white',
                fontWeight: 300,
                fontSize: '8vw',
                pointerEvents: 'none',
                whiteSpace: 'pre-line',
                textAlign: 'center',
                filter: `blur(${blur}px)`,
                width: '100vw',
                maxWidth: 'none',
              }}
            >
              {msg.text}
            </div>
          );
        })}
      </div>
      {/* Message Input stays at the bottom */}
      <MessageInput onSend={async (text) => {
        setLoading(true);
        try {
          await import('firebase/firestore').then(({ collection, addDoc, serverTimestamp }) =>
            addDoc(collection(db, 'messages'), {
              text,
              timestamp: serverTimestamp(),
            })
          );
        } catch (e) {
          alert('Failed to send message. Please try again.');
          console.error('Error adding message:', e);
        } finally {
          setLoading(false);
        }
      }} loading={loading} />
    </div>
  );
}
